INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Открытие участка  "Технологического института" до "Петроградской" в Ленинградском метрополитене $$,
$$ 1 июля 1963 года в Ленинграде открылись две новые станции метро: "Технологический институт-2" и "Петроградская", продлившие Московско-Петроградскую линию. Ключевым новшеством стало создание на "Технологическом институте" первого в СССР кросс-платформенного пересадочного узла, позволявшего удобно пересесть с Кировско-Выборгской линию прямо в одном зале. Станция "Петроградская" стала первой на одноименной стороне города, значительно улучшив транспортную доступность для жителей этого района. Это событие стало важным шагом в развитии ленинградского метрополитена. $$,
'https://ru.wikipedia.org/wiki/%D0%9F%D0%B5%D1%82%D1%80%D0%BE%D0%B3%D1%80%D0%B0%D0%B4%D1%81%D0%BA%D0%B0%D1%8F',
'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a4/Metro_SPB_Line2_Petrogradskaya.jpg/500px-Metro_SPB_Line2_Petrogradskaya.jpg',
1963,
'--07-01'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Открыта первая очередь центрального международного аэропорта «Внуково». $$,
$$ 2 июля 1941 года торжественно открылась первая очередь Центрального аэропорта «Внуково» под Москвой, ставшего первым аэропортом столицы, построенным "с нуля" по специальному проекту. Он был создан как новый мощный воздушный порт для разгрузки существовавшего аэродрома на Ходынском поле и получил название «Московский центральный аэропорт Внуково». Это событие имело огромное значение, так как уже через три недели после открытия аэропорт стал играть критически важную роль в обеспечении воздушных перевозок во время начавшейся Великой Отечественной войны. $$,
'https://ru.wikipedia.org/wiki/%D0%92%D0%BD%D1%83%D0%BA%D0%BE%D0%B2%D0%BE_(%D0%B0%D1%8D%D1%80%D0%BE%D0%BF%D0%BE%D1%80%D1%82)',
'https://upload.wikimedia.org/wikipedia/commons/thumb/4/44/Vnukovo_International_Airport_2019.jpg/500px-Vnukovo_International_Airport_2019.jpg',
1941,
'--07-02'
);
