INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Начало Курской битвы $$,
$$ Начало крупнейшего танкового сражения в истории ("Огненная дуга"), завершившего коренной перелом в войне. $$,
