INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Введение в строй газопровода Саратов - Москва $$,
$$ 11 июля 1946 года был торжественно введен в эксплуатацию первый в СССР магистральный газопровод "Саратов — Москва" протяженностью 843 км. Это событие положило начало созданию Единой системы газоснабжения страны и обеспечило столицу дешевым топливом, что имело огромное значение для послевоенного восстановления экономики и улучшения жизни москвичей. $$,
'https://ru.wikipedia.org/wiki/%D0%93%D0%B0%D0%B7%D0%BE%D0%BF%D1%80%D0%BE%D0%B2%D0%BE%D0%B4_%D0%A1%D0%B0%D1%80%D0%B0%D1%82%D0%BE%D0%B2_%E2%80%94_%D0%9C%D0%BE%D1%81%D0%BA%D0%B2%D0%B0',
'https://e-news.su/uploads/posts/2024-07/1720686062_photo_2024-07-11_12-40-21.jpg',
1946,
'--07-11'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Подписание мирного договора между Литвой и РСФСР $$,
$$ 12 июля 1920 года в Москве был подписан Мирный договор между РСФСР и Литовской Республикой, ставший первым международным актом, в котором Советская Россия официально признала независимость и суверенитет Литвы в её этнографических границах. Договор зафиксировал передачу Литве спорного Виленского края (хотя уже осенью 1920 года Вильнюс был занят Польшей) и установил границу между государствами, положив конец военным действиям. $$,
'https://ru.wikipedia.org/wiki/%D0%9C%D0%BE%D1%81%D0%BA%D0%BE%D0%B2%D1%81%D0%BA%D0%B8%D0%B9_%D0%B4%D0%BE%D0%B3%D0%BE%D0%B2%D0%BE%D1%80_(%D0%A0%D0%A1%D0%A4%D0%A1%D0%A0_%E2%80%94_%D0%9B%D0%B8%D1%82%D0%B2%D0%B0,_1920)',
'https://nashi-dni.su/images/%D0%BD%D1%83%D0%BB%D0%B5%D0%B2%D0%B0%D1%8F_%D0%BF%D0%B0%D0%BF%D0%BA%D0%B0/%D0%9C%D0%BE%D1%81%D0%BA%D0%BE%D0%B2%D1%81%D0%BA%D0%B8%D0%B9_%D0%B4%D0%BE%D0%B3%D0%BE%D0%B2%D0%BE%D1%80_1920_%D0%9B%D0%B0%D1%82%D0%B2%D0%B8%D1%8F.JPG',
1920,
'--07-12'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Начало Львовско-Сандомирской операции $$,
$$ 13 июля 1944 года началась стратегическая Львовско-Сандомирская наступательная операция войск 1-го Украинского фронта под командованием маршала И. С. Конева. Эта мощная операция привела к разгрому немецкой группы армий «Северная Украина», освобождению Западной Украины (включая Львов 27 июля) и созданию крупного Сандомирского плацдарма на западном берегу Вислы в Польше, что открыло путь для дальнейшего наступления на Берлин. $$,
'https://ru.wikipedia.org/wiki/%D0%9B%D1%8C%D0%B2%D0%BE%D0%B2%D1%81%D0%BA%D0%BE-%D0%A1%D0%B0%D0%BD%D0%B4%D0%BE%D0%BC%D0%B8%D1%80%D1%81%D0%BA%D0%B0%D1%8F_%D0%BE%D0%BF%D0%B5%D1%80%D0%B0%D1%86%D0%B8%D1%8F',
'https://upload.wikimedia.org/wikipedia/commons/thumb/2/22/Lvov-Sandomierz_Offensive.jpg/500px-Lvov-Sandomierz_Offensive.jpg',
1944,
'--07-13'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Принятие закона СССР «О государственных пенсиях» $$,
$$ 14 июля 1956 года Верховный Совет СССР принял Закон «О государственных пенсиях», установивший единую общесоюзную систему пенсионного обеспечения для рабочих и служащих. Этот закон впервые гарантировал право на пенсию по старости для мужчин с 60 лет (при стаже 25 лет) и женщин с 55 лет (при стаже 20 лет), заложив основы современной пенсионной системы страны. $$,
'https://sfr.gov.ru/branches/pskov/news~2017/07/14/139031',
'https://sfr.gov.ru/files/branches/pskov/20170714_zakon_o_pensiyah.jpg',
1956,
'--07-14'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
