INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ По дну Ладожского озера проложен трубопровод для подачи топлива в осаждённый Ленинград $$,
$$ 18 июля 1942 года по дну Ладожского озера был введён в эксплуатацию уникальный подводный трубопровод длиной 29 км (из них 21 км под водой) для подачи горючего в осаждённый Ленинград. Спроектированный и проложенный в рекордные сроки (43 дня) под постоянными обстрелами, он стал "артерией жизни", ежесуточно доставляя до 150 тонн бензина и керосина для войск Ленинградского фронта и нужд города. $$,
'https://tatfrontu.ru/news/18-iyulya-1942-goda-po-dnu-ladozhskogo-ozera-prolozhen-truboprovod-dlya-podachi-topliva-v',
'https://tatfrontu.ru/sites/default/files/styles/1600_1600/public/img/2022/07/19/dmgnpeg9fgavjxcwz9x6uj40nbf-dxxc1lq6carwuoneu2ddi9metxepilzzzwkjgaxsqjt3c1dymbivosmdrv4.jpg?itok=Gt91QWNN',
1942,
'--07-18'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Открытие II конгресса Коминтерна $$,
$$ В Петрограде открылся II конгресс Коммунистического Интернационала (Коминтерна), определивший условия вступления партий. $$,
