INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Открытие XXII Летних Олимпийских игр $$,
$$ В Москве открылись первые в истории Олимпийские игры на территории социалистической страны. Проходили до 3 августа. $$,
'https://ru.wikipedia.org/wiki/%D0%9B%D0%B5%D1%82%D0%BD%D0%B8%D0%B5_%D0%9E%D0%BB%D0%B8%D0%BC%D0%BF%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B5_%D0%B8%D0%B3%D1%80%D1%8B_1980',
