INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Пол Маккартни приступил к записи альбома «Снова в СССР» $$,
$$ 20 июля 1987 года Пол Маккартни начал запись альбома «Снова в СССР» (Back in the U.S.S.R.) — сборника каверов на классику рок-н-ролла 1950-х. Этот необычный проект, записанный практически в одиночку (Маккартни сыграл на всех инструментах), стал его личным подарком поклонникам в СССР, где официальная музыка The Beatles долго запрещалась. Альбом, вдохновленный визитом в Москву в 1987 году, изначально распространялся только на территории Советского Союза. $$,
'https://ru.wikipedia.org/wiki/%D0%A1%D0%BD%D0%BE%D0%B2%D0%B0_%D0%B2_%D0%A1%D0%A1%D0%A1%D0%A0',
'https://upload.wikimedia.org/wikipedia/ru/b/ba/%D0%A1%D0%BD%D0%BE%D0%B2%D0%B0_%D0%B2_%D0%A1%D0%A1%D0%A1%D0%A0.png',
1987,
'--07-20'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Первый налёт немецкой авиации на Москву $$,
$$ 21 июля 1941 года произошел первый массированный налёт люфтваффе на Москву, в котором участвовало около 220 немецких бомбардировщиков. Несмотря на разрушения в городе (пострадали Кремль, Театр Вахтангова, жилые кварталы) и гибель мирных жителей, советская ПВО и истребители отразили атаку, сбив не менее 22 самолётов противника. Этот налёт ознаменовал начало пятимесячной Битвы за Москву с воздуха, в ходе которой столица пережила 141 воздушную тревогу. $$,
'https://xn--b1ae4ad.xn--p1ai/calendar/1941-7-21_pervaya-nalet-nemetskoy-aviatsii-na-moskvu',
'https://denvistorii.ru/sites/default/files/2016-07/Bomb_Moscow.jpg',
1941,
'--07-21'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ При пожаре в Ясной Поляне уничтожены рукописи Льва Толстого. $$,
$$ 22 июля 1910 года в усадьбе Ясная Поляна произошел разрушительный пожар в каменном флигеле, где хранился личный архив Льва Николаевича Толстого. Огонь уничтожил бесценные рукописи писателя, включая черновики незавершенных произведений, дневники последних лет и переписку, что стало невосполнимой утратой для мировой литературы. Трагедия произошла через восемь месяцев после ухода Толстого из Ясной Поляны и глубоко потрясла его семью и общественность. $$,
'https://xn--b1ae4ad.xn--p1ai/facts/rukopisi-ne-goryat-uvy-oni-goryat',
'https://upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Lev_Nikolayevich_Tolstoy_in_1910_by_Vladimir_Chertkov.jpg/500px-Lev_Nikolayevich_Tolstoy_in_1910_by_Vladimir_Chertkov.jpg',
1910,
'--07-22'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Запуск космического корабля ''Союз-37'' $$,
$$ 23 июля 1980 года с космодрома Байконур стартовал космический корабль «Союз-37» с международным экипажем: командир Виктор Горбатко (СССР) и космонавт-исследователь Фам Туан (первый космонавт Вьетнама и Юго-Восточной Азии). Основной задачей полета была стыковка с орбитальной станцией «Салют-6» для проведения совместных советско-вьетнамских научных экспериментов, ставшая важным шагом программы «Интеркосмос». Фам Туан стал 138-м космонавтом мира и первым представителем своей страны в космосе. $$,
'https://ru.wikipedia.org/wiki/%D0%A1%D0%BE%D1%8E%D0%B7-37',
'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTxsIpivbu5_ZLfIeBru72DmHdwtdnFgKiBiA&s',
1980,
'--07-23'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ "Кухонные дебаты" $$,
$$ 24 июля 1959 года на Американской национальной выставке в московском парке «Сокольники» вице-президент США Ричард Никсон и Председатель Совета Министров СССР Никита Хрущёв вступили в импровизированную публичную дискуссию у макета типичного американского дома ("кухня"). Их жаркий спор о достоинствах капитализма и социализма, уровня жизни и технологий, транслировавшийся по телевидению, стал одним из самых ярких символов идеологической конфронтации эпохи холодной войны и получил название "Кухонные дебаты". $$,
'https://ru.wikipedia.org/wiki/%D0%9A%D1%83%D1%85%D0%BE%D0%BD%D0%BD%D1%8B%D0%B5_%D0%B4%D0%B5%D0%B1%D0%B0%D1%82%D1%8B',
'https://upload.wikimedia.org/wikipedia/commons/thumb/8/87/1959_Khrushchev%2C_Nixon%2C_Brezhnev.jpg/500px-1959_Khrushchev%2C_Nixon%2C_Brezhnev.jpg',
1959,
'--07-24'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Принятие постановления о введении всеобщего начального обучения. $$,
$$ 25 июля 1930 года ЦК ВКП(б) принял постановление «О всеобщем обязательном начальном обучении», предписывавшее с 1930/1931 учебного года ввести в СССР обязательное бесплатное обучение всех детей (мальчиков и девочек) в возрасте 8–10 лет. Это решение стало ключевым этапом в ликвидации неграмотности и создании единой общедоступной школьной системы, обеспечившей массовую подготовку кадров для индустриализации страны. $$,
'https://www.prlib.ru/history/619410',
'https://russiangiant.ru/wp-content/uploads/2022/07/Rhvxrhnb0PL5bigh1AcS6qhoXa-6ziC8cQjHcqCP5oXWpctNuvMmx6TZ8QWGLv7Kpwn1qdRAjJH9UCDDcsn1YTtM.jpg',
1930,
'--07-25'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Публикация статьи Ленина, призывающей к поражению своего правительства в мировой войне. $$,
$$ 26 июля 1915 года в швейцарской газете «Социал-Демократ» была опубликована программная статья В. И. Ленина «Социализм и война», где он впервые сформулировал стратегию революционного пораженчества: призвал рабочих к превращению империалистической войны в гражданскую, а также к поражению «своего» правительства в Первой мировой войне как пути к свержению царизма. Эта позиция вызвала шок даже среди многих соратников по РСДРП(б) и стала идеологической основой для будущей Октябрьской революции. $$,
'https://von-hoffmann.livejournal.com/2561353.html',
'https://sun1-20.userapi.com/c857036/v857036242/18034b/bxWGYB2bK-8.jpg',
1915,
'--07-26'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Освобождение Львова в ходе Львовско-Сандомирской операции $$,
$$ 27 июля 1944 года войска 1-го Украинского фронта под командованием Маршала И. С. Конева в ходе Львовско-Сандомирской операции освободили Львов от немецко-фашистских захватчиков. Город был взят стремительным штурмом с применением танковых армий (в частности, 3-й гвардейской танковой армии П. С. Рыбалко), что позволило избежать уличных боёв и сохранить архитектурные памятники. Освобождение Львова стало ключевым успехом операции, открывшим путь к Польше и предгорьям Карпат. $$,
'https://ru.wikipedia.org/wiki/%D0%9B%D1%8C%D0%B2%D0%BE%D0%B2%D1%81%D0%BA%D0%BE-%D0%A1%D0%B0%D0%BD%D0%B4%D0%BE%D0%BC%D0%B8%D1%80%D1%81%D0%BA%D0%B0%D1%8F_%D0%BE%D0%BF%D0%B5%D1%80%D0%B0%D1%86%D0%B8%D1%8F',
'https://icdn.lenta.ru/images/2024/07/11/10/20240711104750322/wide_16_9_73051b814bfc1650b16aa98afb1fc0f8.jpg',
1944,
'--07-27'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Многотысячные похороны Владимира Высоцкого в Москве $$,
$$ 28 июля 1980 года в Москве состоялись многотысячные похороны Владимира Высоцкого, собравшие у Театра на Таганке и вдоль пути следования траурной процессии до Ваганьковского кладбища десятки тысяч людей, несмотря на официальное замалчивание события в СМИ из-за проведения Олимпиады-80. Прощание в театре длилось три дня, а спонтанный гражданский ритуал с цветами и стихийными чтениями стихов поэта стал одним из самых массовых проявлений народной любви в СССР к неофициальному кумиру. Это событие, организованное театром и близкими вопреки давлению властей, превратилось в легенду о победе искренней памяти над идеологическими запретами. $$,
'https://ru.wikipedia.org/wiki/%D0%92%D1%8B%D1%81%D0%BE%D1%86%D0%BA%D0%B8%D0%B9,_%D0%92%D0%BB%D0%B0%D0%B4%D0%B8%D0%BC%D0%B8%D1%80_%D0%A1%D0%B5%D0%BC%D1%91%D0%BD%D0%BE%D0%B2%D0%B8%D1%87#%D0%9F%D0%BE%D1%85%D0%BE%D1%80%D0%BE%D0%BD%D1%8B',
'https://cdn.culture.ru/images/ff6057c3-0ba9-57ed-aebe-c0d993a87513/w_1020,c_fill,g_center/1-jpg',
1980,
'--07-28'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Учреждение орденов: Суворова трёх степеней; Кутузова двух степеней; Александра Невского. $$,
$$ 29 июля 1942 года Указом Президиума Верховного Совета СССР были учреждены полководческие ордена: Суворова (I, II и III степени), Кутузова (I и II степени) и Александра Невского. Эти награды, созданные в разгар Сталинградской битвы, предназначались для командного состава Красной Армии за выдающиеся заслуги в планировании и проведении боевых операций, став символом возрождения традиций русской военной славы. $$,
'https://ru.wikipedia.org/wiki/%D0%9E%D1%80%D0%B4%D0%B5%D0%BD_%D0%A1%D1%83%D0%B2%D0%BE%D1%80%D0%BE%D0%B2%D0%B0',
'https://bibliotekar.ru/4-1-78-ordena-velikoy-otechestvennoy-voyny/3.files/image001.jpg',
1942,
'--07-29'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ В Ленинграде открыт стадион им. С. М. Кирова $$,
$$ 30 июля 1950 года в Ленинграде торжественно открылся стадион имени С. М. Кирова на Крестовском острове — крупнейшая спортивная арена СССР своего времени, построенная на месте бывшего Комендантского аэродрома. Грандиозный комплекс в стиле сталинского ампира с колоннадой и 100-тысячными трибунами стал символом возрождения города после блокады и главной площадкой для футбола, легкой атлетики и массовых мероприятий на следующие полвека. $$,
'https://ru.wikipedia.org/wiki/%D0%A1%D1%82%D0%B0%D0%B4%D0%B8%D0%BE%D0%BD_%D0%B8%D0%BC%D0%B5%D0%BD%D0%B8_%D0%9A%D0%B8%D1%80%D0%BE%D0%B2%D0%B0',
'https://upload.wikimedia.org/wikipedia/commons/thumb/7/70/KirovskiyStadion_29140.jpg/500px-KirovskiyStadion_29140.jpg',
1950,
'--07-30'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Начало массовых казней „врагов народа“ по приказу НКВД $$,
$$ 30 июля 1937 года нарком НКВД Н. И. Ежов подписал Приказ № 00447 «Об операции по репрессированию бывших кулаков, уголовников и других антисоветских элементов», санкционировавший массовые аресты и расстрелы по заранее установленным квотам («лимитам»). Эта операция, стартовавшая 5 августа 1937 года, стала ключевым механизмом «Большого террора» — по официальным отчётам НКВД, в ходе неё были осуждены 767?397 человек (включая 386?798 расстрелянных), а реальные цифры жертв могут быть выше. $$,
'https://ru.wikipedia.org/wiki/%D0%9F%D1%80%D0%B8%D0%BA%D0%B0%D0%B7_%D0%9D%D0%9A%D0%92%D0%94_%D0%A1%D0%A1%D0%A1%D0%A0_%E2%84%96_00447',
'https://nkvd.tomsk.ru/content/files/00/da/a4/72eAw6fdQi9U.jpg',
1937,
'--07-30'
);
INSERT INTO events (name, description, source_url, image_url, event_year, event_date) VALUES (
$$ Открытие Центрального стадиона им. В. И. Ленина в Лужниках. $$,
$$ 31 июля 1956 года в Москве торжественно открылся Центральный стадион имени В. И. Ленина в Лужниках — крупнейший спортивный комплекс СССР, построенный в рекордные 450 дней по инициативе Н. С. Хрущёва. Грандиозная арена на 101 тысячу зрителей, возведённая силами комсомольцев на месте бывшей свалки, стала символом «оттепели» и главной площадкой VI Всемирного фестиваля молодёжи 1957 года. $$,
'https://ru.wikipedia.org/wiki/%D0%9B%D1%83%D0%B6%D0%BD%D0%B8%D0%BA%D0%B8_(%D1%81%D1%82%D0%B0%D0%B4%D0%B8%D0%BE%D0%BD)',
'https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/LuzhnikiStadium.jpg/500px-LuzhnikiStadium.jpg',
1956,
'--07-31'
);
